			return cur;
		}
		cur = cur->next;
	}
	printf("没找到啊哦\n");
	return NULL;
}
void ListInsert(ListNode* pos, LTDataType x)
{
	assert(pos);
	ListNode* pre= pos->prev;
	ListNode* newNode = ListCreate(x);
	newNode->next = pos;
	pos->prev = newNode;
	pre->next = newNode;
	newNode->prev = pre;
}
void ListErase(ListNode* pos)
{
	assert(pos);
	ListNode* pre = pos->prev;
	ListNode* next = pos->next;
	pre->next = pos->next;
	next->prev = pre;
	free(pos);
	pos = NULL;
}
